import React, { useState, useEffect } from "react";
import { getTodos, createTodo, deleteTodo } from "../services/api";

export default function Products() {
  const [products, setProducts] = useState([]);
  const [newProductName, setNewProductName] = useState("");
  const [newProductPrice, setNewProductPrice] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Завантаження продуктів при монтуванні
  useEffect(() => {
    loadProducts();
  }, []);

  const loadProducts = async () => {
    setLoading(true);
    setError(null);
    try {
      const data = await getTodos();
      setProducts(data);
    } catch (err) {
      setError("Не вдалося завантажити продукти: " + err.message);
      console.error("Error loading products:", err);
    } finally {
      setLoading(false);
    }
  };

  const handleAddProduct = async (e) => {
    e.preventDefault();
    if (!newProductName.trim() || !newProductPrice) return;

    setLoading(true);
    try {
      const newProduct = {
        name: newProductName,
        price: Number(newProductPrice),
        description: "",
      };
      await createTodo(newProduct);
      setNewProductName("");
      setNewProductPrice("");
      await loadProducts(); // Перезавантажити список
    } catch (err) {
      setError("Не вдалося додати продукт: " + err.message);
      console.error("Error adding product:", err);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteProduct = async (id) => {
    setLoading(true);
    try {
      await deleteTodo(id);
      await loadProducts(); // Перезавантажити список
    } catch (err) {
      setError("Не вдалося видалити продукт: " + err.message);
      console.error("Error deleting product:", err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Products</h2>
      
      {error && <div style={{ color: 'red', marginBottom: '10px' }}>{error}</div>}
      
      {/* Форма додавання */}
      <form onSubmit={handleAddProduct} className="form-container">
        <input
          type="text"
          className="input-text"
          placeholder="Назва продукту..."
          value={newProductName}
          onChange={(e) => setNewProductName(e.target.value)}
          disabled={loading}
        />
        <input
          type="number"
          className="input-text"
          placeholder="Ціна..."
          value={newProductPrice}
          onChange={(e) => setNewProductPrice(e.target.value)}
          disabled={loading}
          style={{ marginLeft: '10px' }}
        />
        <button 
          type="submit" 
          className="input-submit"
          disabled={loading}
        >
          {loading ? 'Завантаження...' : 'Додати'}
        </button>
      </form>

      {/* Список продуктів */}
      {loading && products.length === 0 ? (
        <p>Завантаження...</p>
      ) : (
        <ul style={{ marginTop: 20 }}>
          {products.map((product) => (
            <li key={product._id} className="todo-item">
              <strong>{product.name}</strong> — ${product.price}
              {product.description && <em> ({product.description})</em>}
              <button 
                onClick={() => handleDeleteProduct(product._id)}
                disabled={loading}
                style={{ marginLeft: '10px' }}
              >
                Видалити
              </button>
            </li>
          ))}
        </ul>
      )}

      {products.length === 0 && !loading && (
        <p>Немає продуктів. Додайте перший!</p>
      )}
    </div>
  );
}
